    // 		};
    // 		this.refs.myText.value = '';
    // 		this.props.addNewTask(obj);
    // 	}
    // }

    const handleAddTask = () => {
        if (inputValue === "") return;

        const newTask = {
            id: _.parseInt(list.length ? list[list.length - 1].id : 0) + 1,
            content: inputValue,
        };

        addTodo(newTask).then(() => {
            setList([...list, newTask]);
            setInputValue("");
        });
    };


    return (
        <div className="dialog">
            <div>
                <h3>添加</h3>
                <input type="text" className="task-input" ref="myText" placeholder="请输入"/>
            </div>
            <div>
                <input type="button" className="add-btn" value="提交" onClick={this.handleClick}/>
            </div>


            <input
                className="task-input"
                type="text"
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                data-testid="task-input"
            />
            <button className="submit-button" onClick={handleAddTask} data-testid="add-button">
                提交
            </button>
        </div>

    );

}

export default Dialog;
