    // 			content: value,
    // 		};
    // 		this.refs.myText.value = '';
    // 		this.props.addNewTask(obj);
    // 	}
    // }


    return (
        <div className="dialog">
            <div>
                <h3>添加</h3>
                <input type="text" className="task-input" value={inputValue}
					   onChange={(e) => setInputValue(e.target.value)} placeholder="请输入"/>
            </div>
            <div>
                <input type="button" className="submit-button" data-testid="add-button" value="提交" onClick={addNewTask}/>
            </div>
        </div>
    );
};

export default Dialog;
