import React, { Component } from 'react';

class ListItem extends Component {
	constructor (props) {
		super(props);
		this.state={
			showInput:false,
			valueCon:'',
		}
		this.handleFinished = this.handleFinished.bind(this);
		this.handleDelete = this.handleDelete.bind(this);
		this.handleUpdate = this.handleUpdate.bind(this);
	} 

	handleFinished () {
		var status = this.props.item.status;

		status = (status === 0 ? 1 : 0);

		var obj = {
			id: this.props.item.id,
			name: this.props.item.name,
			status: status
		}
		
		this.props.finishedChange(obj);	
	}

	handleDelete () {
		this.props.totalChange(this.props.item);
	}

	handleUpdate (){
		var oldHtml = document.getElementsByClassName("update");
		var newInput = document.createElement('input');
		newInput.type = "text";
		newInput.value = oldHtml;
		this.state.valueCon=oldHtml;
		oldHtml = '';
		newInput.setSelectionRange(0, this.state.valueCon.length)
		newInput.focus();
		newInput.onblur = function () {
			oldHtml = this.value == oldHtml? oldHtml : this.value;
		}
		var obj = {
			id: this.props.item.id,
			name: this.props.item.name,
			status: this.props.item.status
		}

		this.props.finishedChange(obj);
	}

	render () {
		const item = this.props.item;

		const unfinish = {
			backgroundColor: '#DFFCB5',
			color: '#2EB872',
		};

		const finish = {
			backgroundColor: '#FFFA9D',
			color: '#FF9A3C',
			textDecoration: 'line-through'
		}

		var itemStyle = item.status === 0 ? unfinish : finish;
		
		return (
			<li key={item.id} style={itemStyle}>
				<span 
					onClick={this.handleFinished} 
					id={item.id}
					className="check-btn"
					style={{backgroundColor: item.status === 0 ? '#fff' : '#A1EAFB'}}
				></span>
				{!this.state.showInput?
					<span className="update-btn" onClick={this.handleChangeClick}>
						修改
                    </span>
					:
					<div className="write_input">
						<div className="write_input_name">
							<input placeholder="请输入"
								   defaultValue={this.props.value==='-'?'':this.props.value}
								   onChange={this.handleChange}
								   style={{height:'25px',margin:'0'}}
							/>
						</div>
						<div className="write_input_hide">
							<span type="check-circle" className="write_input_hide_yes" onClick={this.handleAffirmClick}>yes</span>
							<span type="close-circle" className="write_input_hide_no" onClick={this.handleCloseClick}>no</span>
						</div>
					</div>
				}
				<span onClick={this.handleUpdate} className="update">{ item.name }</span>
				<span onClick={this.handleDelete} className="delete-btn">删除</span>
				{/*<span onClick={this.handleUpdate} className="update-btn">修改</span>*/}
			</li>
		);
	}
}

export default ListItem;
